package net.azib.java.students.t020632;

/**
 * Cycle
 *
 * @author t020632
 */
public class Cycle {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		
		while(i <= 10){
			System.out.println(i);
			i++;
		}
		
		i = 1;
		
		do{
			System.out.println(i);
			i++;
		}while(i <= 10);
		
	}

}
